import { combineReducers } from "redux";
import { BUY_POKEMON, RETURN_POKEMON } from "../actions/types";

const initialState = {
    amount: 0,
};

const buyPokemonReducers = (state = initialState, action) => {
    switch (action.type) {
        case BUY_POKEMON: {
            return {
                ...state,
                amount: state - 1,
            };
        }
        case RETURN_POKEMON: {
            return {
                ...state,
                amount: state + 1,
            };
        }
        default:
            return state;
    }
};

export default combineReducers({
    buyPokemon: buyPokemonReducers,
});
